00250 bool LocalStorage::activate(bool) {
00251   _memory.begin(memory_name, true);
00252   _device_id = _memory.getUShort(key_device_id, D_DEVICE_ID);
00253   if(_memory.getString(key_ap_password, _ap_password, CONFIG_VAL_MAX) == 0) {
00254     strcpy(_ap_password, D_ACCESS_POINT_PASSWORD);
00255   }
00256   if(_memory.getString(key_wifi_ssid, _wifi_ssid, CONFIG_VAL_MAX) == 0) {
00257     strcpy(_wifi_ssid, D_WIFI_SSID);
00258   }
00259   if(_memory.getString(key_wifi_password, _wifi_password, CONFIG_VAL_MAX) == 0) {
00260     strcpy(_wifi_password, D_WIFI_PASSWORD);
00261   }
00262   if(_memory.getString(key_api_key, _api_key, CONFIG_VAL_MAX) == 0) {
00263     strcpy(_api_key, D_APIKEY);
00264   }
00265   _fixed_longitude = _memory.getDouble(key_fixed_longitude, 0);
00266   _fixed_latitude = _memory.getDouble(key_fixed_latitude, 0);
00267   _fixed_range = _memory.getUInt(key_fixed_range, 0);
00268   _last_longitude = _memory.getDouble(key_last_longitude, 0);
00269   _last_latitude = _memory.getDouble(key_last_latitude, 0);
00270   _memory.end();
00271   return true;
00272 }
