00274 int8_t LocalStorage::handle_produced_work(const worker_map_t& workers) {
00275   // Get reading data to store
00276   const auto& gps = (GpsConnector*) workers.at(k_worker_gps_connector);
00277   if(gps->is_fresh() && gps->get_data().location_valid) {
00278     set_last_latitude(gps->get_data().latitude, false);
00279     set_last_longitude(gps->get_data().longitude, false);
00280     return Handler::e_handler_data_handled;
00281   }
00282   return Handler::e_handler_idle;
00283 }
